// 		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
// 		current.setAbortAction(true);
// 	}

	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);

	// If the isDatePast method returns true, the date is in the past.
	if (isPast == true) {
		gs.addErrorMessage("NeedItUtils : When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

	var isToday = niutils.isDateToday(current.u_when_needed);

	if(isToday == true) {
		gs.addErrorMessage("You cannot submit NeedIt request for today.");
	}

	//alert(niutils.calcDuration(current.u_when_needed));

	current.u_duration.value = niutils.calcDuration(current.u_when_needed);


})(current, previous);
